  const btn = () => {
    console.log(anime);
  }
  return(
    <div className="description">
        {
            full ? 
            <div className="descriptionContainer">
            <Link to={"/"+review.title}>
              <img className="pokemonImage" src={review.image} alt={review.name} width={200} />
            </Link>
               <h2>{review.title}</h2>
                <p>
                    {review.content} 
                </p>  
                Comments:
                {/* <small>{
                review.comments.map(comment => {
                    return <div><p> {comment} </p></div>
                })

                }</small>   */}
                    <br/>
                    <Link to={'/anime/all'} ><Button variant='danger'>X</Button></Link>  
            </div>
                 : null
        }

    </div>

)
}


export default Post;
