import { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Button, Form } from 'react-bootstrap';



const RegisterForm = ({addAnime}) => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('Action');
    // const [review, setReview] = useState('No reviews available')


    const addAnimeBtnClickHandler = () => {
        console.log('email')
    //     axios.post('http://localhost:8000/anime', {
    //         email,
    //         password
    //         // review
    // })
    //     .then(res => {

    //         const newAnime = res.data;
    //         alert(`${res.data.title} has been added to your list`)
    //         addAnime(newAnime);

    //     });
    }
    return(
        <div className="addAnimeForm">        
        <Form>
            <Form.Group value={email} onChange={(e)=>{setEmail(e.target.value)}} className="mb-3" controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" />
                <Form.Text className="text-muted">
                We'll never share your email with anyone else.
                </Form.Text>
            </Form.Group>

            <Form.Group value={password} onChange={(e)=>{setPassword(e.target.value)}} className="mb-3" controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" />
            </Form.Group>
            <Button variant='primary' onClick={addAnimeBtnClickHandler} style={{margin:'2%'}}> 
                Register
            </Button>
        </Form>
        </div>
    );
}

export default RegisterForm;
