													key={pokemon.id} 
													name={pokemon.name} 
													image={pokemon.img} 
													type={pokemon.types}/>
												})
											}
										</Route>

										< Route exact path="/type/Fairy">
											{
												this.state.pokemons.filter( (pokemon) => {
													return pokemon.types.indexOf('Fairy') !== -1
												})  
												.map( pokemon => {
													return  <Pokemon
													key={pokemon.id} 
													name={pokemon.name} 
													image={pokemon.img} 
													type={pokemon.types}/>
												})
											}
										</Route>


										</tbody>
						</table>
						<div className="descriptionContainer">
							{
											this.state.pokemons.map( pokemon => <Route path={"/"+pokemon.name}>
												<Description  prevPage={this.state.prevPage} pokemon={pokemon} full={true} />
											</Route>)
										}
						</div>

  		</header>


    </div>
  );
  }


export default App;
