import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Action = (props) => {

    const [actionAnime, setActionAnime] = useState([])

    useEffect((_id) => {
		axios('http://localhost:8000/anime/action')
		.then(res => {
		  setActionAnime(res.data)
		//   console.log(animes)
		})
	  }, []);

    const checker = () => {
        console.log(actionAnime.title)
    }
    return (
        <div>
             <tr>
            <td>
                <button className="EditDeleteBtn" onClick={() => { props.deleteBtnClickHandler(actionAnime._id) }}> Delete Entry </button> <br />
                <Link to={"/" + actionAnime.title}>
                    <img src={actionAnime.image} alt={actionAnime.title} width="200px" />
                </Link>
                {/* <Link to={'/anime/edit/' + actionAnime._id}> */}
                    <button
                        className="EditDeleteBtn"
                        onClick={() => { checker() }}
                    >Edit Entry</button>
                {/* </Link> */}

            </td>
            <td>
                {actionAnime.title} </td>
            <td> Rating: {actionAnime.rating} <br />
                {/* <Link to={'/anime/addreview/' + actionAnime._id}>
                    <button onClick={props.editAnimeBtnClickHandler(actionAnime)} className="EditDeleteBtn">Add Review</button>
                </Link> */}
                <Link to={'/review/' + actionAnime.title}>
                    <button >Read Review</button>
                </Link>

            </td>

        </tr>
        </div>

    );
}

export default Action;
