import { useState, useEffect } from 'react';
import axios from 'axios';
import Action from './Action';

const AnimeList = ({anime, deleteAnime, editAnimeBtnClickHandler,}) => {


  const [actionAnime, setActionAnime] = useState([])

  useEffect((_id) => {
		axios('http://localhost:8000/anime/action')
		.then(res => {
		  setActionAnime(res.data)
		//   console.log(animes)
		})
	  }, []);

  const deleteBtnClickHandler =(_id) => {
    axios.delete('http://localhost:8000/anime/' + _id)
    .then(res => {
      deleteAnime(_id);
      alert(`${res.data.title} has been deleted`)
    })
  }

  // const 
    return (
      <div>
        <table>
          <tr>      
              {
                actionAnime.map(anime => {
                  return <Action
                  key={anime._id}
                  actionAnime={anime}
                  deleteAnime={deleteAnime}
                  editAnimeBtnClickHandler={editAnimeBtnClickHandler}
                  />
                  })

                }	
          </tr>
        </table>
      </div>


    )
}

export default AnimeList;
