import { Link } from 'react-router-dom';

const Action = (props) => {
    const checker = () => {
        console.log(props.actionAnime)
    }
    return (
        <div>
             <tr>
            <td>
                <button className="EditDeleteBtn" onClick={() => { props.deleteBtnClickHandler(props.actionAnime._id) }}> Delete Entry </button> <br />
                <Link to={"/" + props.actionAnime.title}>
                    <img src={props.actionAnime.image} alt={props.actionAnime.title} width="200px" />
                </Link>
                {/* <Link to={'/anime/edit/' + actionAnime._id}> */}
                    <button
                        className="EditDeleteBtn"
                        onClick={() => { checker() }}
                    >Edit Entry</button>
                {/* </Link> */}

            </td>
            <td>
                {props.actionAnime.title} </td>
            <td> Rating: {props.actionAnime.rating} <br />
                {/* <Link to={'/anime/addreview/' + actionAnime._id}>
                    <button onClick={props.editAnimeBtnClickHandler(actionAnime)} className="EditDeleteBtn">Add Review</button>
                </Link> */}
                <Link to={'/review/' + props.actionAnime.title}>
                    <button >Read Review</button>
                </Link>

            </td>

        </tr>
        </div>

    );
}

export default Action;
