import './App.css';
import React from 'react'
import AnimeList from './components/AnimeList';
import { Route, Link } from 'react-router-dom';
import Description from './components/Description';
import NavBar from './components/NavBar';
import AddAnimeForm from './components/AddAnimeForm';
import Review from './components/Review';
import AddReviewForm from './components/AddReviewForm';
import EditAnimeForm from './components/EditAnimeForm';
import 'bootstrap/dist/css/bootstrap.min.css'
import {Button, Card, Table} from 'react-bootstrap';
import axios from 'axios';
import {useState, useEffect} from 'react';

const App = () => {

	const [animes, setAnimes] = useState([])
	const [actionAnime, setActionAnime] = useState([])
	const [editAnime, setEditAnime] = useState({});
	const [editReview, setEditReview] = useState("")
	const [prevPage, setPrevPage] = useState('/all');

	useEffect((_id) => {
		axios('http://localhost:8000/anime')
		.then(res => {
		  setAnimes(res.data)
		})
	  }, [animes]);

const deleteAnime = (_id) => {
	let updatedAnime = animes.filter(anime => anime._id !== _id);
	setAnimes(updatedAnime);
};

const addAnime = (newAnime) => {
	setAnimes([
		...animes,
		newAnime
	])
}

const changePrevPage = (page) => {
	setPrevPage({
		prevPage: page
	});
};

const editAnimeBtnClickHandler = (anime) => {
	setEditAnime(anime)
	// console.log(anime)
}

const addReviewBtnClickHandler = (anime) => {
	setEditReview(anime)
	// console.log(anime)
}



    return (
    <div className="App">
		<img className="pokeheader" src={"https://i.pinimg.com/564x/98/0c/e1/980ce1fc4da397d69b6b1d1a656fccdf.jpg"} />
		<h1>Anime Bakari</h1>
		<NavBar />
      <header className="App-header">

			<div>
				<Route exact path={'/addanime'}>
					<AddAnimeForm addAnime={addAnime} />
				</Route>

				<Route path={'/anime/edit'}>
					<EditAnimeForm editAnime={editAnime} editAnimeBtnClickHandler={editAnimeBtnClickHandler}/>
				</Route>	
				<Route path={'/anime/addreview'}>
					<AddReviewForm editReview={editReview} addReviewBtnClickHandler={addReviewBtnClickHandler}/>
				</Route>								
						</div>
						<div className="descriptionContainer">
							{
							animes.map( anime => 
								<Route exact path={"/review/"+anime.title}>
									<Review  key={anime._id} prevPage={prevPage} anime={anime} full={true} />
								</Route>)
							}
						</div>
						<div className="descriptionContainer">
							{
							animes.map( anime => 
								<Route path={"/"+anime.title}>
									<Description key={anime._id} prevPage={prevPage} anime={anime} full={true} />
								</Route>)
							}
						</div>
						<Table variant="dark">

								<th>Image</th>
								<th>Image</th>
								<th>Image</th>

								<tbody>
									{/* <Card>
										<Card.Body>
											<Card.Title> Card example </Card.Title>
										</Card.Body>
									</Card> */}
									{/* <Button variant="primary"> Test Button </Button> */}
									<Route exact path="/all">
										{
										animes.map(anime => {
											return <AnimeList 
											key={anime._id}
											anime={anime}
											deleteAnime={deleteAnime}
											editAnimeBtnClickHandler={editAnimeBtnClickHandler}
											addReviewBtnClickHandler={addReviewBtnClickHandler}/>
											})

										}	
									</Route>

									< Route exact path="/anime/action">
											{

												animes.filter( (anime) => {
													return anime.category.indexOf('Action') !== -1
												})  
												.map( anime => {
													return <AnimeList 
														key={anime._id}
														anime={anime}
														deleteAnime={deleteAnime}
														editAnimeBtnClickHandler={editAnimeBtnClickHandler}/>
												})
											}
										</Route>

										< Route exact path="/anime/drama">
											{

												animes.filter( (anime) => {
													return anime.category.indexOf('Drama') !== -1
												})  
												.map( anime => {
													return <AnimeList 
														key={anime._id}
														anime={anime}
														deleteAnime={deleteAnime}
														editAnimeBtnClickHandler={editAnimeBtnClickHandler}
														addReviewBtnClickHandler={addReviewBtnClickHandler}/>
												})
											}
										</Route>

										< Route exact path="/anime/comedy">
											{

												animes.filter( (anime) => {
													return anime.category.indexOf('Comedy') !== -1
												})  
												.map( anime => {
													return <AnimeList 
														key={anime._id}
														anime={anime}
														deleteAnime={deleteAnime}
														editAnimeBtnClickHandler={editAnimeBtnClickHandler}
														addReviewBtnClickHandler={addReviewBtnClickHandler}/>
												})
											}
										</Route>
										< Route exact path="/anime/horror.gore">
											{

												animes.filter( (anime) => {
													return anime.category.indexOf('Horror/Gore') !== -1
												})  
												.map( anime => {
													return <AnimeList 
														key={anime._id}
														anime={anime}
														deleteAnime={deleteAnime}
														editAnimeBtnClickHandler={editAnimeBtnClickHandler}
														addReviewBtnClickHandler={addReviewBtnClickHandler}/>
												})
											}
										</Route>

										</tbody>
						</Table>


  		</header>


    </div>
  );
  }


export default App;
