import React from 'react';
import { Route, Link } from 'react-router-dom'
import axios from 'axios';

const AnimeList = ({anime, deleteAnime, editAnimeBtnClickHandler,addReviewBtnClickHandler}) => {

  const deleteBtnClickHandler =(_id) => {
    axios.delete('http://localhost:8000/anime/' + _id)
    .then(res => {
      deleteAnime(_id);
      alert(`${res.data.title} has been deleted`)
    })
  }

  // const 
    return (

         <tr> 
              <td> 
                <button className="EditDeleteBtn" onClick={()=>{deleteBtnClickHandler(anime._id)}}> Delete Entry </button> <br />
                <Link to={"/" + anime.title}>
                  <img src={anime.image} alt={anime.title} width="200px" /> 
                </Link> 
                <Link to={'/anime/edit/' + anime._id}>
                  <button 
                  className="EditDeleteBtn"
                  onClick={()=> {editAnimeBtnClickHandler(anime)}}
                 >Edit Entry</button>
                </Link>

              </td>
              <td>
                {anime.title} </td>
              <td> Rating: {anime.rating} <br />
                <Link to={'/anime/addreview/' + anime._id}>
                  <button onClick={addReviewBtnClickHandler(anime)} className="EditDeleteBtn">Add Review</button>
                </Link>
                <Link to={'/review/' + anime.title}>
                  <button className="EditDeleteBtn" >Read Review</button>
                </Link>

              </td>  

          </tr>





    )
}

export default AnimeList;
