import React from 'react';
import { Route, Link } from 'react-router-dom'
import axios from 'axios';

const AnimeList = ({anime, deleteAnime}) => {
  const deleteBtnClickHandler =(_id) => {
    axios.delete('http://localhost:8000/anime/' + _id)
    .then(res => {
      deleteAnime(_id);
      alert('deleted')
    })
  }
    return (

         <tr> 
              <td> 
                <Link to={"/" + anime.title}>
                  <img src={anime.image} alt={anime.title} width="200px" /> 
                </Link> 
                <button onClick={()=>{deleteBtnClickHandler(anime._id)}}> Delete Entry </button>
                <Link to={'/anime/edit/' + anime._id}>
                  <button>Edit Entry</button>
                </Link>

              </td>
              <td>
                {anime.title} </td>
              <td> Rating: {anime.rating}
              <Link to={'/review/' + anime.title}>
                  <button >Read Review</button>
                </Link>

              </td>  

          </tr>





    )
}

export default AnimeList;
